  <!-- if: ${minion.dead} -->\n            <li>${minion.name}已经死亡</li>\n            <!-- /if -->\n            <!-- /for -->\n        </ul>\n    </div>\n</div>\n<div class="scene-friend">\n    <div class="scene-title">友方信息</div>\n    <!-- use: scene-hero(data = ${hero}) -->\n    <div class="scene-minions">\n        <!-- for: ${friendlyMinions} as ${minion} -->\n        <!-- if: !${minion.dead} -->\n        <!-- use: scene-minion(data = ${minion}) -->\n        <!-- /if -->\n        <!-- /for -->\n    </div>\n    <div class="scene-dead">\n        <div class="scene-title">坟场</div>\n        <ul>\n            <!-- for: ${friendlyMinions} as ${minion} -->\n            <!-- if: ${minion.dead} -->\n            <li>${minion.name}已经死亡</li>\n            <!-- /if -->\n            <!-- /for -->\n        </ul>\n    </div>\n</div>\n\n<!-- target: scene-hero -->\n<div class="scene-hero">\n    <p class="scene-title">${data.name}</p>\n    <p>\n        <!-- if: ${data.hp} <= 0 -->\n        已阵亡\n        <!-- else -->\n        <label>血量：<span class="scene-red">${data.hp}</span></label>\n        <label>护甲：<span class="scene-red">${data.armor}</span></label>\n        <!-- /if -->\n    </p>\n    <p>\n        <label>法力：<span class="scene-red">${data.mp}</span></label>\n        <label>伤害：<span class="scene-red">${data.ap}</span></label>\n    </p>\n    <p>共有<span class="scene-yellow">${data.cards}</span>张手牌，牌库剩<span class="scene-yellow">${data.stack}</span>张</p>\n    <!-- if: ${data.drawn} || ${data.overflow}-->\n    <p>\n        共抽取<span class="scene-yellow">${data.drawn}</span>张牌\n        <!-- if: ${data.overflow}-->\n        ，共爆掉<span class="scene-yellow">${data.overflow}</span>张牌\n        <!-- /if -->\n    </p>\n    <!-- /if -->\n    <!-- var: secret = ${data.secret} && ${data.secret}.join(\'，\') -->\n    <!-- if: ${secret} -->\n    <p>奥秘：${secret}</p>\n    <!-- /if -->\n</div>\n\n<!-- target: scene-minion -->\n<div class="scene-minion">\n    <p class="scene-minion-title">${data.name}</p>\